def hour_minute_format(x):
    ts = x.total_seconds()
    hours, remainder = divmod(ts, 3600)
    minutes, seconds = divmod(remainder, 60)
    return ("{:02d}:{:02d}").format(int(hours), int(minutes))


def get_rgb(bucket):
    if bucket == "GOOD":
        return [133, 239, 71]
    elif bucket == "SATISFACTORY":
        return [0, 189, 86]
    elif bucket == "MODERATE":
        return [255, 195, 0]
    elif bucket == "POOR":
        return [232, 99, 10]
    elif bucket == "VERY POOR":
        return [255, 23, 0]
    else:
        return [123, 17, 58]


def PM25_color(x):
    if x <= 30:
        return "GOOD"
    elif x <= 60:
        return "SATISFACTORY"
    elif x <= 90:
        return "MODERATE"
    elif x <= 120:
        return "POOR"
    elif x <= 250:
        return "VERY POOR"
    else:
        return "SEVERE"


def PM10_color(x):
    if x <= 50:
        return "GOOD"
    elif x <= 100:
        return "SATISFACTORY"
    elif x <= 250:
        return "MODERATE"
    elif x <= 350:
        return "POOR"
    elif x <= 430:
        return "VERY POOR"
    else:
        return "SEVERE"


def NO2_color(x):
    if x <= 40:
        return "GOOD"
    elif x <= 80:
        return "SATISFACTORY"
    elif x <= 180:
        return "MODERATE"
    elif x <= 280:
        return "POOR"
    elif x <= 400:
        return "VERY POOR"
    else:
        return "SEVERE"


def SO2_color(x):
    if x <= 40:
        return "GOOD"
    elif x <= 80:
        return "SATISFACTORY"
    elif x <= 380:
        return "MODERATE"
    elif x <= 800:
        return "POOR"
    elif x <= 1600:
        return "VERY POOR"
    else:
        return "SEVERE"


def CO_color(x):
    if x <= 1.0:
        return "GOOD"
    elif x <= 2.0:
        return "SATISFACTORY"
    elif x <= 10:
        return "MODERATE"
    elif x <= 17:
        return "POOR"
    elif x <= 34:
        return "VERY POOR"
    else:
        return "SEVERE"


def O3_color(x):
    if x <= 50:
        return "GOOD"
    elif x <= 100:
        return "SATISFACTORY"
    elif x <= 168:
        return "MODERATE"
    elif x <= 208:
        return "POOR"
    elif x <= 748:
        return "VERY POOR"
    else:
        return "SEVERE"


